                pinkTromboneElement.tongue.index.value = tongue_indexes[i]
                pinkTromboneElement.tongue.diameter.value = tongue_diams[i]
                lipConstriction.diameter.value = lip_diams[i]
                tractConstriction.index.value = constriction_indexes[i]
                tractConstriction.diameter.value = constriction_diams[i]
                throatConstriction.diameter.value = throat_diams[i]

